import{confirmMessage,errorMessage,successMessage}from"../../services/notification.service.js";import{removeChat}from"../../services/chat.service.js";const removeChatButtons=document.querySelectorAll(".remove-chat-button"),chatRoomIdNumber=document.querySelectorAll(".chat-room-id");for(let o=0;o<removeChatButtons.length;o++){const b="/removeChat";removeChatButtons[o].addEventListener("click",()=>{confirmMessage("You sure?").then(e=>{null!==e&&removeChat(b,+chatRoomIdNumber[o].value).then(e=>{successMessage(e.message.value),setTimeout(()=>{location.reload()},1500)}).catch(e=>{errorMessage(e.errorCode+" error: "+e.errorMessage)})})})}